late_decision &lt;- function(ref, accuracy) { sapply(ref, function(r) { if(runif(1) &lt; accuracy) r else ifelse(r == &quot;Accept&quot;, &quot;Reject&quot;, &quot;Accept&quot;) }) } # Operators with different accuracy levels op1_t1 &lt;- simulate_decision(reference, 0.92) op1_t2 &lt;- simulate_decision(reference, 0.92) op2_t1 &lt;- simulate_decision(reference, 0.88) op2_t2 &lt;- simulate_decision(reference, 0.88) op3_t1 &lt;- simulate_decision(reference, 0.95) op3_t2 &lt;- simulate_decision(reference, 0.95) # Calculate agreement metrics # 1. Within-operator agreement (repeatability) within_op1 &lt;- mean(op1_t1 == op1_t2) within_op2 &lt;- mean(op2_t1 == op2_t2) within_op3 &lt;- mean(op3_t1 == op3_t2) # 2. Each operator vs. reference op1_vs_ref &lt;- mean(op1_t1 == reference &amp; op1_t2 == reference) op2_vs_ref &lt;- mean(op2_t1 == reference &amp; op2_t2 == reference) op3_vs_ref &lt;- mean(op3_t1 == reference &amp; op3_t2 == reference) # 3. All operators agree (both trials) all_agree_t1 &lt;- mean(op1_t1 == op2_t1 &amp; op2_t1 == op3_t1) all_agree_both &lt;- mean(op1_t1 == op1_t2 &amp; op1_t1 == op2_t1 &amp; op2_t1 == op2_t2 &amp; op2_t1 == op3_t1 &amp; op3_t1 == op3_t2) cat(&quot;=== Attribute Agreement Analysis ===

